import { useState } from 'react';
import loginImg from '../img/avatar.png'
import './style.scss'


const LoginForm = ({ Login, error }) => {

  const [details, setDetails] = useState({
    email: '',
    password: ''
  })

  const submitHandler =e => {
    e.preventDefault();
    Login(details);
  }
  
  const onChange = e => {

  }

  return (
    <div className="base-container">
      {/* {ERROR} */}
      <div className="image">
        <img src={loginImg} alt="" />
      </div>
      <div className="header">Login</div>
      <div className="content">
        <form onsubmit={submitHandler} className="form">
        <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input value={details.email} onChange={onChange} type="email" name="email" placeholder="email"/>
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input value={details.password} onChange={onChange} type="password" name="password" placeholder="password"/>
          </div>
        </form>
        <p>Not registerd? <a href="/register" className='link'> Register here!</a></p>
      </div>
      <div className="footer">
        <button type="button" className="btn">Login</button>
      </div>
    </div>
  );
}

export default LoginForm
